(function($){

    $('.gform_footer').on('click', 'a.x-btn', function(e){
        e.preventDefault();
        $(e.delegateTarget).find('[type="submit"]').trigger('click');
    });

    // =====================
    // = mobile menu =
    // =====================

    $("a.navtoggle").click(function(e){
            e.preventDefault();
            $("#nav").animate({right: 0});
            $("#site-wrapper").animate({left: -260});
    });

    $("a#close").click(function(e){
            e.preventDefault();
            $("#nav").animate({right: -260});
            $("#site-wrapper").animate({left: 0});
    });

    // toggle navbar-toggle icon on click
    $("div.navbar-header").on('click', "button.navbar-toggle", function( event ) {
        event.preventDefault();
        $("button span").toggleClass("fa-bars fa-times");
    });

    // left slide menu
    var trigger = $('.hamburger'),
        overlay = $('.overlay'),
       isClosed = false;

    function buttonSwitch() {

        if (isClosed === true) {
            overlay.hide();
            trigger.removeClass('is-open');
            trigger.addClass('is-closed');
            isClosed = false;
        } else {
            overlay.show();
            trigger.removeClass('is-closed');
            trigger.addClass('is-open');
            isClosed = true;
        }
    }

    trigger.click(function () {
        buttonSwitch();
    });

    $('[data-toggle="offcanvas"]').click(function () {
        $('#wrapper').toggleClass('toggled');
    });

})(jQuery);

